using UnityEngine;public class Glass2 : MonoBehaviour{	[Range(-0.03f, 0.03f)] public float m_RefractionDensity = -0.03f;	public bool m_Bump = false;	public Texture2D m_BumpTex;	public float m_BumpTexScale = 6f;	Renderer m_Rd;		void Start ()	{		m_Rd = GetComponent<Renderer> ();		Material[] mats = m_Rd.materials;		for (int i = 0; i < mats.Length; i++)		{			mats[i].shader = Shader.Find ("Crystal Glass/Screen Space/Transparent");			mats[i].DisableKeyword ("CRYSTAL_GLASS_BUMP");			mats[i].SetFloat ("_Density", m_RefractionDensity);		}	}	void Update ()	{		Material[] mats = m_Rd.materials;		for (int i = 0; i < mats.Length; i++)		{			if (m_Bump)				mats[i].EnableKeyword ("CRYSTAL_GLASS_BUMP");			else				mats[i].DisableKeyword ("CRYSTAL_GLASS_BUMP");			mats[i].SetFloat ("_Density", m_RefractionDensity);			mats[i].SetTexture ("_BumpMap", m_BumpTex);			mats[i].SetTextureScale ("_BumpMap", new Vector2 (m_BumpTexScale, m_BumpTexScale));		}	}}